def get_csrf(html):
    m = re.search(
        r'<input[^>]+name="_csrf"[^>]+value="([^"]+)"',
        html
    )
    if not m:
        raise RuntimeError("CSRF token not found in page")
    return m.group(1)

def get_calendar_json(response):
    soup = BeautifulSoup(response, "html.parser")

    result = []

    tables = soup.select("table.calendar-table")

    for table in tables:
        h4 = table.find("h4")
        if not h4:
            continue

        month = h4.get_text(strip=True)

        month_entry = {
            "month": month,
            "days": []
        }

        tds = table.find_all("td")

        for td in tds:
            spans = td.find_all("span")

            if not spans:
                continue

            # first span is the day number (may be empty)
            day_text = spans[0].get_text(strip=True)

            if not day_text.isdigit():
                continue

            day = int(day_text)

            events = []
            for s in spans[1:]:
                txt = s.get_text(strip=True)
                if txt:
                    events.append(txt)

            month_entry["days"].append({
                "day": day,
                "events": events
            })

        result.append(month_entry)

    return result

    
